package avans.avd.models


data class User(
    val id: Long = NEW_USER_ID,
    val username: String,
    val password: String,
    val email: String,
    val avatar: String = DEFAULT_IMAGE,
    val role: Role = Role.USER,
) {
    companion object {
        const val NEW_USER_ID = 0L
        // Base64 of Kotlin Kodee mascot
        const val DEFAULT_IMAGE = ""
    }
}

enum class Role {
    USER,
    OFFICIAL,
    ADMIN,
    ANONYMOUS
}

fun Role.isQualifiedOfficial() = this == Role.OFFICIAL || this == Role.ADMIN